"use strict";(self.webpackChunkskyrimnet_docs=self.webpackChunkskyrimnet_docs||[]).push([[6945],{1608:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"tutorialSidebar":[{"type":"category","label":"Features","items":[{"type":"link","label":"Overview","href":"/SkyrimNet-GamePlugin/Features/overview","docId":"Features/overview","unlisted":false},{"type":"link","label":"Events and Reactions","href":"/SkyrimNet-GamePlugin/Features/events-and-reactions","docId":"Features/events-and-reactions","unlisted":false},{"type":"link","label":"GameMaster","href":"/SkyrimNet-GamePlugin/Features/gamemaster","docId":"Features/gamemaster","unlisted":false},{"type":"link","label":"Player Dialogue","href":"/SkyrimNet-GamePlugin/Features/player-dialogue","docId":"Features/player-dialogue","unlisted":false},{"type":"link","label":"Action System","href":"/SkyrimNet-GamePlugin/Features/action-system","docId":"Features/action-system","unlisted":false},{"type":"link","label":"Item and Spell Customization","href":"/SkyrimNet-GamePlugin/Features/customization","docId":"Features/customization","unlisted":false},{"type":"link","label":"Overrides System","href":"/SkyrimNet-GamePlugin/Features/overrides","docId":"Features/overrides","unlisted":false},{"type":"link","label":"Game Data Explorer","href":"/SkyrimNet-GamePlugin/Features/gamedataexplorer","docId":"Features/gamedataexplorer","unlisted":false},{"type":"link","label":"Custom Triggers","href":"/SkyrimNet-GamePlugin/Features/customtriggers","docId":"Features/customtriggers","unlisted":false},{"type":"link","label":"Voice Samples","href":"/SkyrimNet-GamePlugin/Features/voicesamples","docId":"Features/voicesamples","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Installation Guide","items":[{"type":"link","label":"Dependencies","href":"/SkyrimNet-GamePlugin/Installation Guide/dependencies","docId":"Installation Guide/dependencies","unlisted":false},{"type":"link","label":"SkyrimNet Installation","href":"/SkyrimNet-GamePlugin/Installation Guide/skyrimnet-installation","docId":"Installation Guide/skyrimnet-installation","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Large Language Models","items":[{"type":"link","label":"Model Agents","href":"/SkyrimNet-GamePlugin/Large Language Models/model-agents","docId":"Large Language Models/model-agents","unlisted":false},{"type":"link","label":"LLM Tutorial","href":"/SkyrimNet-GamePlugin/Large Language Models/openrouter","docId":"Large Language Models/openrouter","unlisted":false},{"type":"link","label":"Suggested LLMs","href":"/SkyrimNet-GamePlugin/Large Language Models/suggested-llms","docId":"Large Language Models/suggested-llms","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Memory System","items":[{"type":"link","label":"SQLite storage","href":"/SkyrimNet-GamePlugin/Memory System/sqlite-storage","docId":"Memory System/sqlite-storage","unlisted":false},{"type":"link","label":"Memory System","href":"/SkyrimNet-GamePlugin/Memory System/memory-recall","docId":"Memory System/memory-recall","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Prompts","items":[{"type":"link","label":"System Prompt","href":"/SkyrimNet-GamePlugin/Prompts/prompts","docId":"Prompts/prompts","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"NPC Bios","items":[{"type":"link","label":"Dynamic Bios","href":"/SkyrimNet-GamePlugin/NPC Bios/dynamic-bios","docId":"NPC Bios/dynamic-bios","unlisted":false},{"type":"link","label":"Character Static Bios","href":"/SkyrimNet-GamePlugin/NPC Bios/static-bios","docId":"NPC Bios/static-bios","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Text to Speech","items":[{"type":"link","label":"Getting Started with TTS","href":"/SkyrimNet-GamePlugin/Text to Speech/tts-getting-started","docId":"Text to Speech/tts-getting-started","unlisted":false},{"type":"link","label":"Piper","href":"/SkyrimNet-GamePlugin/Text to Speech/piper","docId":"Text to Speech/piper","unlisted":false},{"type":"link","label":"XTTS","href":"/SkyrimNet-GamePlugin/Text to Speech/xtts","docId":"Text to Speech/xtts","unlisted":false},{"type":"link","label":"Zonos","href":"/SkyrimNet-GamePlugin/Text to Speech/zonos","docId":"Text to Speech/zonos","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Speech to Text","items":[{"type":"link","label":"Local Whisper","href":"/SkyrimNet-GamePlugin/Speech to Text/local-whisper","docId":"Speech to Text/local-whisper","unlisted":false},{"type":"link","label":"Whisper openai","href":"/SkyrimNet-GamePlugin/Speech to Text/whisper-openai","docId":"Speech to Text/whisper-openai","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Vast.ai","items":[{"type":"link","label":"Vast ai","href":"/SkyrimNet-GamePlugin/Vast.ai/vast-ai","docId":"Vast.ai/vast-ai","unlisted":false},{"type":"link","label":"Images","href":"/SkyrimNet-GamePlugin/Vast.ai/images","docId":"Vast.ai/images","unlisted":false}],"collapsed":true,"collapsible":true}],"apiSidebar":[{"type":"category","label":"SkyrimNet API","items":[{"type":"link","label":"API","href":"/SkyrimNet-GamePlugin/Skyrimnet API/api","docId":"Skyrimnet API/api","unlisted":false},{"type":"link","label":"Library","href":"/SkyrimNet-GamePlugin/Skyrimnet API/library","docId":"Skyrimnet API/library","unlisted":false},{"type":"link","label":"Decorators","href":"/SkyrimNet-GamePlugin/Skyrimnet API/decorators","docId":"Skyrimnet API/decorators","unlisted":false}],"collapsed":true,"collapsible":true}]},"docs":{"Features/action-system":{"id":"Features/action-system","title":"Action System","description":"The Custom Action System in SkyrimNet allows you to expose any Papyrus function as an AI-accessible action, turning existing mod functionality into intelligent, context-aware behaviors. It\u2019s designed for both mod authors and non-programmers, with a clear visual interface and zero need to write code.","sidebar":"tutorialSidebar"},"Features/customization":{"id":"Features/customization","title":"Item and Spell Customization","description":"SkyrimNet supports rich customization of both items and spells, enabling immersive and dynamic roleplay through custom names and descriptions. These customizations are fully exposed to the language model system, meaning NPCs and the world can react intelligently to what the player or nearby NPCs are wearing or casting.","sidebar":"tutorialSidebar"},"Features/customtriggers":{"id":"Features/customtriggers","title":"Custom Triggers","description":"The Schema-Aware Trigger System in SkyrimNet allows you to define custom reactions to in-game events. This system makes NPCs, the player character, and the world itself more dynamic, offering immersive responses that feel tailored to what\'s happening in real time.","sidebar":"tutorialSidebar"},"Features/events-and-reactions":{"id":"Features/events-and-reactions","title":"Events and Reactions","description":"SkyrimNet\u2019s event system enables dynamic memory, LLM-based dialogue, and NPC reactivity by processing structured in-game events. These events act as the core data stream that drives memory formation, conversation context, and AI behavior.","sidebar":"tutorialSidebar"},"Features/gamedataexplorer":{"id":"Features/gamedataexplorer","title":"Game Data Explorer","description":"The Game Data Explorer is a powerful real-time web interface for browsing and manipulating Skyrim\u2019s entire game data stack \u2014 including both vanilla content and loaded mods. It allows modders, developers, and advanced users to inspect and modify data across all major categories with precision and speed.","sidebar":"tutorialSidebar"},"Features/gamemaster":{"id":"Features/gamemaster","title":"GameMaster","description":"The GameMaster system in SkyrimNet enables dynamic, AI-driven scene narration, ambient storytelling, and autonomous world control. It acts as a virtual Dungeon Master, capable of responding to gameplay events or narrating scenes based on recent context.","sidebar":"tutorialSidebar"},"Features/overrides":{"id":"Features/overrides","title":"Overrides System","description":"\ud83c\udf9b Overrides System","sidebar":"tutorialSidebar"},"Features/overview":{"id":"Features/overview","title":"Overview","description":"Overview","sidebar":"tutorialSidebar"},"Features/player-dialogue":{"id":"Features/player-dialogue","title":"Player Dialogue","description":"This system allows the player to express internal thoughts, spoken reactions, and context-aware dialogue in response to game events, driven by LLM prompts. It blends narrative depth with character voice, enabling SkyrimNet to act like a true interactive roleplay engine.","sidebar":"tutorialSidebar"},"Features/voicesamples":{"id":"Features/voicesamples","title":"Voice Samples","description":"The Voice Samples section, in the web ui , provides a fast and powerful interface to browse and manage voice data for any NPC in Skyrim. It is primarily used for voice cloning with Zonos TTS and, in the future, XTTS. This system supports rapid audio previewing and selection, enabling high-quality, consistent voice matching for dynamic AI-generated speech.","sidebar":"tutorialSidebar"},"Installation Guide/dependencies":{"id":"Installation Guide/dependencies","title":"Dependencies","description":"SkyrimNet depends on several tools and libraries to function effectively, both for regular gameplay and optional GPU acceleration.","sidebar":"tutorialSidebar"},"Installation Guide/skyrimnet-installation":{"id":"Installation Guide/skyrimnet-installation","title":"SkyrimNet Installation","description":"SkyrimNet is easy to set up and flexible to use, whether you\'re running it locally or leveraging cloud services.","sidebar":"tutorialSidebar"},"intro":{"id":"intro","title":"Welcome to SkyrimNet","description":"Welcome to the official documentation for SkyrimNet!"},"Large Language Models/model-agents":{"id":"Large Language Models/model-agents","title":"Model Agents","description":"SkyrimNet uses specialized language models for different tasks that collectively simulate intelligent NPC behavior. These models coordinate to deliver a rich, reactive Skyrim experience, from dialogue to memory, action selection, and emotional response.","sidebar":"tutorialSidebar"},"Large Language Models/openrouter":{"id":"Large Language Models/openrouter","title":"LLM Tutorial","description":"SkyrimNet \u2013 Understanding LLMs for Roleplay","sidebar":"tutorialSidebar"},"Large Language Models/suggested-llms":{"id":"Large Language Models/suggested-llms","title":"Suggested LLMs","description":"---","sidebar":"tutorialSidebar"},"Memory System/memory-recall":{"id":"Memory System/memory-recall","title":"Memory System","description":"SkyrimNet\u2019s memory system gives NPCs long-term, context-aware memory \u2014 enabling immersive behaviors, dynamic relationships, and narrative consistency. Each memory is generated from actual in-game events and can influence dialogue, emotional state, or behavior.","sidebar":"tutorialSidebar"},"Memory System/sqlite-storage":{"id":"Memory System/sqlite-storage","title":"SQLite storage","description":"SkyrimNet uses a persistent, lightweight SQLite database to store NPC memories, enabling characters to remember events across sessions \u2014 even after saving, quitting, or rebooting the game.","sidebar":"tutorialSidebar"},"Mod Integrations":{"id":"Mod Integrations","title":"Mod Integrations","description":""},"NPC Bios/Bios Generation":{"id":"NPC Bios/Bios Generation","title":"Bios Generation","description":""},"NPC Bios/dynamic-bios":{"id":"NPC Bios/dynamic-bios","title":"Dynamic Bios","description":"The Dynamic Bios System in SkyrimNet provides a way for NPC character biographies to evolve based on in-game events, relationships, and world changes. It works as a layered system that preserves static baseline identities while dynamically updating details to reflect growth and current context.","sidebar":"tutorialSidebar"},"NPC Bios/static-bios":{"id":"NPC Bios/static-bios","title":"Character Static Bios","description":"\ud83d\udd27 What Is It?","sidebar":"tutorialSidebar"},"Prompts/prompts":{"id":"Prompts/prompts","title":"System Prompt","description":"","sidebar":"tutorialSidebar"},"Skyrimnet API/api":{"id":"Skyrimnet API/api","title":"API","description":"","sidebar":"apiSidebar"},"Skyrimnet API/decorators":{"id":"Skyrimnet API/decorators","title":"Decorators","description":"------------------------------","sidebar":"apiSidebar"},"Skyrimnet API/library":{"id":"Skyrimnet API/library","title":"Library","description":"","sidebar":"apiSidebar"},"Speech to Text/local-whisper":{"id":"Speech to Text/local-whisper","title":"Local Whisper","description":"Local Whisper STT is SkyrimNet\u2019s offline speech recognition system, powered by OpenAI\u2019s open-source Whisper model. It enables players to speak naturally through a microphone and converts their voice into text entirely on-device, with no audio ever sent to the cloud.","sidebar":"tutorialSidebar"},"Speech to Text/whisper-openai":{"id":"Speech to Text/whisper-openai","title":"Whisper openai","description":"SkyrimNet supports OpenAI Whisper as a cloud-based speech-to-text (STT) service. When enabled, this mode sends your microphone input to OpenAI\u2019s servers for transcription, and receives highly accurate text in return.","sidebar":"tutorialSidebar"},"Text to Speech/piper":{"id":"Text to Speech/piper","title":"Piper","description":"\ud83d\udde3\ufe0f Piper TTS in SkyrimNet","sidebar":"tutorialSidebar"},"Text to Speech/tts-getting-started":{"id":"Text to Speech/tts-getting-started","title":"Getting Started with TTS","description":"\ud83d\udde3\ufe0f What is Text-to-Speech (TTS)?","sidebar":"tutorialSidebar"},"Text to Speech/xtts":{"id":"Text to Speech/xtts","title":"XTTS","description":"\ud83e\udde0 XTTS in SkyrimNet \u2014 the Default-Quality TTS","sidebar":"tutorialSidebar"},"Text to Speech/zonos":{"id":"Text to Speech/zonos","title":"Zonos","description":"\ud83d\udde3\ufe0f High-Fidelity TTS for SkyrimNet","sidebar":"tutorialSidebar"},"Vast.ai/images":{"id":"Vast.ai/images","title":"Images","description":"","sidebar":"tutorialSidebar"},"Vast.ai/vast-ai":{"id":"Vast.ai/vast-ai","title":"Vast ai","description":"Vast.ai is a marketplace for renting GPU computing power at low cost from providers around the world. It\'s widely used for AI, machine learning, 3D rendering, and other compute-heavy tasks \u2014 including real-time voice synthesis and LLM inference. It allows you to have high quality STT and TTS for SkyrimNet for $0.07-$0.15 per hour of use (billed by the minute, only when playing).","sidebar":"tutorialSidebar"}}}}')}}]);